const item = $input.first();
const contenido = item.json.message?.content || item.json.output || item.json.text || "";

// Buscamos algo que parezca un JSON (entre llaves)
const jsonMatch = contenido.match(/\{[\s\S]*\}/);

let resultado = { respuesta: "" };

if (jsonMatch) {
  try {
    resultado = JSON.parse(jsonMatch[0]);
  } catch (e) {
    // Si falla el parseo, devolvemos el texto original como respuesta
    resultado = { respuesta: contenido };
  }
} else {
  // Si no se encontró ningún JSON, devolvemos el texto original
  resultado = { respuesta: contenido };
}

return { json: resultado };

// 1. INPUTS
const puntosRaw = $('Cargar puntos verdes').all().map(item => item.json);
const body = $('Chat Entrada').first().json;
// Nos aseguramos de leer el mensaje, venga de donde venga
const consulta = body.mensaje || body.consulta || body.body?.mensaje || ""; 

// 2. LIMPIEZA DE DATOS (Miniatura)
// Creamos una lista ligera con TODOS los puntos.
// Quitamos coordenadas y datos irrelevantes para que entre todo en el prompt.
const todosLosPuntos = puntosRaw.map(p => ({
    id: p.id,
    n: p.nombre,          // Nombre
    d: p.direccion,       // Dirección
    b: p.barrio,          // Barrio
    h: p.dia_hora,        // Horario
    m: p.materiales       // Materiales
}));

// 3. SALIDA DIRECTA
return {
    json: {
        usuario_dice: consulta,
        base_datos_puntos: todosLosPuntos // ¡Le mandamos todo!
    }
};
